import { TestBed } from '@angular/core/testing';
import { AddComponent } from './add.component';
describe('AddComponent', () => {
    let component;
    let fixture;
    beforeEach(async () => {
        await TestBed.configureTestingModule({
            declarations: [AddComponent]
        })
            .compileComponents();
    });
    beforeEach(() => {
        fixture = TestBed.createComponent(AddComponent);
        component = fixture.componentInstance;
        fixture.detectChanges();
    });
    it('El formulario deberia ser invalido', async () => {
        const fixture = TestBed.createComponent(AddComponent);
        const app = fixture.componentInstance;
        fixture.detectChanges();
        const firstName = app.contactForm.controls['firstName'];
        firstName.setValue('Pedro el matematico');
        expect(app.contactForm.invalid).toBeTrue();
    });
});
